package com.sky.encryption;

import jdk.nashorn.internal.runtime.regexp.joni.Config;
import sun.misc.BASE64Decoder;
import sun.misc.BASE64Encoder;

import java.io.*;

/**
 * @author : wushikai
 * <p>
 * date : 2020-06-16
 */
public class Base64Convert {


    /**
     */
    public static String GetImageStr(String path)
    {
        //将图片文件转化为字节数组字符串，并对其进行Base64编码处理
        //待处理的图片
        String imgFile;
        imgFile = path;
        InputStream in = null;
        byte[] data = null;
        //读取图片字节数组
        try
        {
            in = new FileInputStream(imgFile);
            data = new byte[in.available()];
            in.read(data);
            in.close();
        }
        catch (IOException e)
        {
            e.printStackTrace();
        }
        //对字节数组Base64编码
        BASE64Encoder encoder = new BASE64Encoder();
        //返回Base64编码过的字节数组字符串
        return encoder.encode(data);
    }
    /*
     */
    public static boolean GenerateImage(String imgStr,String filename)
    {
        //对字节数组字符串进行Base64解码并生成图片
        //图像数据为空
        if (imgStr == null)
            return false;


        BASE64Decoder decoder = new BASE64Decoder();
        try
        {
            //Base64解码
            byte[] b = decoder.decodeBuffer(imgStr);
            for(int i=0;i<b.length;++i)
            {
                if(b[i]<0)
                {
                    //调整异常数据
                    b[i]+=256;
                }
            }
            //生成jpeg图片
            String imagePath="d:\\test\\";
            //System.currentTimeMillis()
            //新生成的图片
            String imgFilePath = imagePath+filename;
            OutputStream out = new FileOutputStream(imgFilePath);
            out.write(b);
            out.flush();
            out.close();
            return true;
        }
        catch (Exception e)
        {
            return false;
        }
    }



    public static void main(String[] args){
        String path="GetImageStr";
        String imageStr = GetImageStr("d:\\test\\Wu_sky@foxmail.com.png");
        System.out.println("imageStr = " + imageStr);
        GenerateImage(imageStr, "myMail.png");


    }

}


